mapper.select:
SELECT userid   ,userpassword   ,username ,   userphone,    useremail
FROM tb_user 
WHERE userid = #{userid} and userpassword = #{userpassword}

mapper.update:
UPDATE tb_user 
SET userpassword   = #{userpassword}
      , username     = #{username}
      , userphone = #{userphone}
      , useremail  = #{useremail}
       WHERE 1 = 1
